"use strict";
const express = require("express");
const app = express();
const users = require("./application/user/userroute");
const admin = require("./application/admin/adminroute");
const school = require("./application/school/schoolroute");
const classes = require("./application/class/classroute");
const student = require("./application/student/studentroute");
const staff = require("./application/staff/staffRoute");
const role = require("./application/roles/roles_Route");
const school_admin = require("./application/school_admin/school_adminRoute");
const staff_class = require("./application/staff_class/s_cRoute");
const staff_role = require("./application/staff_role/s_rRoute");
app.use('/user', users);
app.use('/admin', admin);
app.use('/school', school);
app.use('/class', classes);
app.use('/student', student);
app.use('/staff', staff);
app.use('/roles', role);
app.use('/school_admin', school_admin);
app.use('/staff_class', staff_class);
app.use('/staff_role', staff_role);
module.exports = app;
